import React, { useState } from "react";

const MeetingForm = ({ clients, onSave, onCancel, editingMeeting }) => {
  const [client, setClient] = useState(editingMeeting?.client || "");
  const [date, setDate] = useState(editingMeeting ? new Date(editingMeeting.date).toISOString().split('T')[0] : "");
  const [time, setTime] = useState(editingMeeting ? new Date(editingMeeting.date).toTimeString().slice(0, 5) : "");
  const [notes, setNotes] = useState(editingMeeting?.notes || "");
  const [recurring, setRecurring] = useState(editingMeeting?.recurring || false);
  const [recurringType, setRecurringType] = useState(editingMeeting?.recurringType || "weekly");

  const handleSubmit = (e) => {
    e.preventDefault();
    const meetingDateTime = new Date(`${date}T${time}`);
    
    onSave({
      client,
      date: meetingDateTime,
      notes,
      recurring,
      recurringType: recurring ? recurringType : null
    });
    
    // Reset form
    setClient("");
    setDate("");
    setTime("");
    setNotes("");
    setRecurring(false);
    setRecurringType("weekly");
  };

  return (
    <form className="space-y-3 bg-gray-50 p-4 rounded border" onSubmit={handleSubmit}>
      <h3 className="text-lg font-semibold mb-3">
        {editingMeeting ? 'Edit Meeting' : 'Schedule New Meeting'}
      </h3>
      
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">Client</label>
        <select 
          className="border p-2 w-full rounded" 
          value={client} 
          onChange={e => setClient(e.target.value)} 
          required
        >
          <option value="">Select Client</option>
          {clients.map(c => (
            <option key={c._id} value={c._id}>{c.name}</option>
          ))}
        </select>
      </div>

      <div className="grid grid-cols-2 gap-3">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
          <input 
            className="border p-2 w-full rounded" 
            type="date" 
            value={date} 
            onChange={e => setDate(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">Time</label>
          <input 
            className="border p-2 w-full rounded" 
            type="time" 
            value={time} 
            onChange={e => setTime(e.target.value)} 
            required 
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">Notes</label>
        <textarea 
          className="border p-2 w-full rounded" 
          placeholder="Meeting agenda, topics to discuss, etc." 
          value={notes} 
          onChange={e => setNotes(e.target.value)} 
          rows="3"
        />
      </div>

      <div className="flex items-center space-x-3">
        <label className="flex items-center">
          <input 
            type="checkbox" 
            checked={recurring} 
            onChange={e => setRecurring(e.target.checked)}
            className="mr-2"
          />
          <span className="text-sm">Recurring Meeting</span>
        </label>
        
        {recurring && (
          <select 
            className="border p-1 rounded text-sm" 
            value={recurringType} 
            onChange={e => setRecurringType(e.target.value)}
          >
            <option value="weekly">Weekly</option>
            <option value="biweekly">Bi-weekly</option>
            <option value="monthly">Monthly</option>
          </select>
        )}
      </div>

      <div className="flex gap-2 pt-2">
        <button 
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600" 
          type="submit"
        >
          {editingMeeting ? 'Update Meeting' : 'Schedule Meeting'}
        </button>
        <button 
          className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600" 
          type="button"
          onClick={onCancel}
        >
          Cancel
        </button>
      </div>
    </form>
  );
};

export default MeetingForm;
