import jsPDF from 'jspdf';

export const generateInvoicePDF = (invoice, client) => {
  const doc = new jsPDF();
  
  // Set font
  doc.setFont('helvetica');
  
  // Header
  doc.setFontSize(24);
  doc.setFont('helvetica', 'bold');
  doc.text('INVOICE', 105, 20, { align: 'center' });
  
  // Company Info
  doc.setFontSize(12);
  doc.setFont('helvetica', 'bold');
  doc.text('ClientPulse', 20, 40);
  doc.setFont('helvetica', 'normal');
  doc.setFontSize(10);
  doc.text('Freelance Management System', 20, 50);
  doc.text('Email: contact@clientpulse.com', 20, 60);
  doc.text('Phone: +1 (555) 123-4567', 20, 70);
  
  // Invoice Details
  doc.setFont('helvetica', 'bold');
  doc.text('Invoice Details:', 20, 90);
  doc.setFont('helvetica', 'normal');
  doc.text(`Invoice #: ${invoice._id.slice(-8).toUpperCase()}`, 20, 100);
  doc.text(`Date: ${new Date().toLocaleDateString()}`, 20, 110);
  doc.text(`Due Date: ${new Date(invoice.dueDate).toLocaleDateString()}`, 20, 120);
  doc.text(`Status: ${invoice.status}`, 20, 130);
  
  // Client Info
  doc.setFont('helvetica', 'bold');
  doc.text('Bill To:', 120, 90);
  doc.setFont('helvetica', 'normal');
  doc.text(client ? client.name : 'Unknown Client', 120, 100);
  doc.text(client ? client.email : 'No email', 120, 110);
  
  // Line separator
  doc.setDrawColor(200, 200, 200);
  doc.line(20, 140, 190, 140);
  
  // Invoice Items
  doc.setFont('helvetica', 'bold');
  doc.text('Description', 20, 155);
  doc.text('Amount', 150, 155);
  
  doc.setFont('helvetica', 'normal');
  doc.text(invoice.description || 'Professional Services', 20, 170);
  doc.text(`$${invoice.amount.toFixed(2)}`, 150, 170);
  
  // Line separator
  doc.line(20, 180, 190, 180);
  
  // Total
  doc.setFont('helvetica', 'bold');
  doc.text('Total:', 130, 195);
  doc.text(`$${invoice.amount.toFixed(2)}`, 150, 195);
  
  // Footer
  doc.setFontSize(8);
  doc.setFont('helvetica', 'normal');
  doc.text('Thank you for your business!', 105, 250, { align: 'center' });
  doc.text('Please make payment within 30 days of invoice date.', 105, 255, { align: 'center' });
  
  // Save the PDF
  const fileName = `invoice-${invoice._id.slice(-8)}-${client ? client.name.replace(/\s+/g, '-') : 'client'}.pdf`;
  doc.save(fileName);
};
