import React, { useState } from "react";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL + "/login";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(API_URL, { username, password });
      localStorage.setItem("token", res.data.token);
      setError("");
      onLogin();
    } catch (err) {
      setError("Invalid credentials");
    }
  };

  return (
    <form className="space-y-2" onSubmit={handleSubmit}>
      <input className="border p-2 w-full" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
      <input className="border p-2 w-full" type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
      <button className="bg-blue-500 text-white px-3 py-1 rounded" type="submit">Login</button>
      {error && <div className="text-red-500">{error}</div>}
    </form>
  );
};

export default Login;
