import React, { useState, useEffect } from "react";
import axios from "axios";
import ClientForm from "./ClientForm";
import ClientList from "./ClientList";
import AutomationPanel from "./AutomationPanel";

const API_URL = process.env.REACT_APP_API_URL + "/clients";

const Clients = () => {
  const [clients, setClients] = useState([]);
  const [showAutomation, setShowAutomation] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");
      const response = await axios.get(API_URL, {
        headers: { Authorization: token }
      });
      setClients(response.data);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    } finally {
      setLoading(false);
    }
  };

  const addClient = async (clientData) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(API_URL, clientData, {
        headers: { Authorization: token }
      });
      setClients([...clients, response.data]);
      setShowForm(false);
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    }
  };

  const deleteClient = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: token }
      });
      setClients(clients.filter(client => client._id !== id));
      setError("");
    } catch (err) {
      setError(err.response?.data?.error || err.message);
    }
  };

  const handleClientCreated = (newClient) => {
    fetchClients();
  };

  const getClientStats = () => {
    const totalClients = clients.length;
    const activeClients = clients.filter(c => c.tags?.includes('ongoing')).length;
    const newClients = clients.filter(c => {
      const clientDate = new Date(c.createdAt || Date.now());
      const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
      return clientDate > thirtyDaysAgo;
    }).length;

    return { totalClients, activeClients, newClients };
  };

  const stats = getClientStats();

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-lg text-gray-600">Loading clients...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-800">Clients</h1>
          <p className="text-gray-600 mt-1">Manage your client relationships and information</p>
        </div>
        <div className="flex gap-3">
          <button
            onClick={() => setShowAutomation(!showAutomation)}
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
          >
            {showAutomation ? 'Hide Automation' : 'ðŸ¤– Automation'}
          </button>
          <button
            onClick={() => setShowForm(!showForm)}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
          >
            {showForm ? 'Cancel' : '+ Add Client'}
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Clients</p>
              <p className="text-3xl font-bold text-blue-600">{stats.totalClients}</p>
            </div>
            <div className="text-3xl text-blue-600">ðŸ‘¥</div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Active Clients</p>
              <p className="text-3xl font-bold text-green-600">{stats.activeClients}</p>
            </div>
            <div className="text-3xl text-green-600">ðŸš€</div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">New This Month</p>
              <p className="text-3xl font-bold text-purple-600">{stats.newClients}</p>
            </div>
            <div className="text-3xl text-purple-600">ðŸ“ˆ</div>
          </div>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      )}

      {/* Automation Panel */}
      {showAutomation && (
        <div className="bg-white rounded-lg shadow p-6">
          <AutomationPanel onClientCreated={handleClientCreated} />
        </div>
      )}

      {/* Add Client Form */}
      {showForm && (
        <div className="bg-white rounded-lg shadow p-6">
          <ClientForm onSave={addClient} />
        </div>
      )}

      {/* Clients List */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-800">All Clients</h2>
          <p className="text-gray-600 mt-1">
            {clients.length} client{clients.length !== 1 ? 's' : ''} total
          </p>
        </div>

        {clients.length === 0 ? (
          <div className="p-12 text-center">
            <div className="text-6xl mb-4">ðŸ‘¥</div>
            <h3 className="text-xl font-semibold text-gray-800 mb-2">No clients yet</h3>
            <p className="text-gray-600 mb-6">Get started by adding your first client or using automation to capture clients from platforms.</p>
            <div className="flex gap-3 justify-center">
              <button
                onClick={() => setShowForm(true)}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Add First Client
              </button>
              <button
                onClick={() => setShowAutomation(true)}
                className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
              >
                Try Automation
              </button>
            </div>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Client
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Source
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Tags
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Added
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {clients.map((client) => (
                  <tr key={client._id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="flex-shrink-0 h-10 w-10">
                          <div className="h-10 w-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-medium">
                            {client.name.charAt(0).toUpperCase()}
                          </div>
                        </div>
                        <div className="ml-4">
                          <div className="text-sm font-medium text-gray-900">{client.name}</div>
                          <div className="text-sm text-gray-500">{client.email}</div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                          client.source === 'LinkedIn' ? 'bg-blue-100 text-blue-800' :
                          client.source === 'Upwork' ? 'bg-green-100 text-green-800' :
                          client.source === 'Fiverr' ? 'bg-purple-100 text-purple-800' :
                          client.source === 'Email' ? 'bg-orange-100 text-orange-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {client.source || 'Manual'}
                        </span>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex flex-wrap gap-1">
                        {client.tags && client.tags.length > 0 ? (
                          client.tags.map((tag, index) => (
                            <span
                              key={index}
                              className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                                tag === 'VIP' ? 'bg-yellow-100 text-yellow-800' :
                                tag === 'ongoing' ? 'bg-green-100 text-green-800' :
                                tag === 'potential' ? 'bg-blue-100 text-blue-800' :
                                'bg-gray-100 text-gray-800'
                              }`}
                            >
                              {tag}
                            </span>
                          ))
                        ) : (
                          <span className="text-gray-400 text-sm">No tags</span>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        {client.autoGenerated ? (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                            Auto-generated
                          </span>
                        ) : (
                          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                            Manual
                          </span>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {new Date(client.createdAt || Date.now()).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      <div className="flex gap-2">
                        <button className="text-blue-600 hover:text-blue-900">View</button>
                        <button className="text-green-600 hover:text-green-900">Edit</button>
                        <button 
                          onClick={() => deleteClient(client._id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Clients;
