import React, { useState } from "react";
import Dashboard from "./components/Dashboard";
import Clients from "./components/Clients";
import Invoices from "./components/Invoices";
import Meetings from "./components/Meetings";
import Login from "./components/Login";
import Signup from "./components/Signup";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token"));
  const [showSignup, setShowSignup] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleSignup = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-blue-600 text-white p-4 text-2xl font-bold">ClientPulse</header>
      <main className="p-4">
        {!isLoggedIn ? (
          <div className="max-w-sm mx-auto mt-10">
            {showSignup ? (
              <>
                <Signup onSignup={handleSignup} />
                <div className="text-center mt-4">
                  <button className="text-blue-600 underline" onClick={() => setShowSignup(false)}>
                    Already have an account? Login
                  </button>
                </div>
              </>
            ) : (
              <>
                <Login onLogin={handleLogin} />
                <div className="text-center mt-4">
                  <button className="text-blue-600 underline" onClick={() => setShowSignup(true)}>
                    Don't have an account? Sign Up
                  </button>
                </div>
              </>
            )}
          </div>
        ) : (
          <div className="grid gap-6 grid-cols-1 md:grid-cols-2 lg:grid-cols-4">
            <Dashboard />
            <Clients />
            <Invoices />
            <Meetings />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
